mapper.select:
    	/* getMemberList :: 관리자 회원 리스트 조회 */
		SELECT *, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT_YMD, DATE_FORMAT(LAST_LOGIN_DT, '%Y-%m-%d') AS LAST_LOGIN_DT_YMD
		  FROM member
		 WHERE ENABLE = 'Y';

    	/* getMemberInfo :: 관리자 회원 정보 조회 */
		SELECT *, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT_YMD, DATE_FORMAT(LAST_LOGIN_DT, '%Y-%m-%d') AS LAST_LOGIN_DT_YMD
		  FROM member
		 WHERE MEM_NUM = #{memNum}

mapper.update:
	/* memberdelete :: 회원 정보 삭제 */
	UPDATE member SET
           ENABLE = 'N'
     WHERE MEM_NUM = #{memNum}
