mapper.insert:
/* memberinsert :: 회원 추가 */
INSERT INTO member(USER_ID, USER_PW, NICK_NAME, EMAIL, STATUS, GENDER, ENABLE, USER_TYPE, REG_DT)
VALUES (#{userId}, #{userPw}, #{nickName}, #{email}, 'D', #{gender}, 'Y', 'A', NOW());

mapper.select:
    	/* findid :: 아이디 찾기 */
		SELECT USER_ID
		FROM member
		WHERE email = #{email}
