mapper.select:
    	/* getThisMemberInfo :: 현재 사용자 정보 조회 */
		SELECT *
		  FROM member
		 WHERE MEM_NUM = #{memNum}

    	/* getScrapList :: 스크랩 리스트 조회 */
		SELECT *
		, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT_YMD
		, SUBSTR(TITLE, 1, 40) AS TITLE_20
		FROM bboom_board
		WHERE U_NAME= #{uName}

mapper.update:
    	/* quitMember :: 회원탈퇴 */
    	UPDATE member
    	   SET ENABLE = "N"	
		 WHERE MEM_NUM = #{memNum}
