mapper.select:
SELECT *, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT_YMD
  FROM site_board
WHERE SEQ = #{seq}

SELECT count(*)
FROM board_scrap
WHERE REG_ID=#{regId} AND LINK_SEQ=#{linkSeq}

SELECT *, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT_YMD
FROM board_comment
WHERE LINK_SEQ=#{linkSeq}

SELECT *, DATE_FORMAT(REG_DT, '%Y-%m-%d') AS REG_DT_YMD
FROM board_feedback
WHERE LINK_SEQ=#{linkSeq}
